import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
    {
        path : '/business', //访问路径
        name : 'home',
        component: () => import('@/App') //对应组件
    },
    {
        path : '/business/order', //访问路径
        name : 'order',
        component: () => import('@/components/test.vue') //对应组件
    },
]

const router = new VueRouter({
    mode: 'history',
    routes: routes
})

export default router

