import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

// 2. 定义一些路由
// 每个路由都需要映射到一个组件。
// 我们后面再讨论嵌套路由。
const routes = [
    { path: '/order', component: "@/components/HelloWorld" },
]

const router = new VueRouter({
    routes // (缩写) 相当于 routes: routes
})

/*
export default new Router({
    routes: [
        {
            path : '/order',//访问路径
            name : 'home',
            component: () => import('@/components/HelloWorld') //对应组件
        },

    ],
    mode: "history" //去除访问时候带#问题
})
*/
