import Vue from 'vue'
import Router from 'vue-router'

Vue.use(Router)

export default new Router({
    routes: [
        {
            path : '/',//访问路径
            name : 'home',
            component: () => import('@/components/home') //对应组件
        },

    ],
    mode: "history" //去除访问时候带#问题
})
